mapper.select:
select count(*)
from t_user
where phone = #{phone}

select *
from t_user
where u_id = #{u_id}

mapper.update:
update t_user


        , u_name=#{u_name}


        , u_password=#{u_password}


        , u_real_name=#{u_real_name}


        , u_phone=#{u_phone}


        , u_address=#{u_address}


where u_id=#{u_id}

mapper.select:
select *
from t_tickets
where u_id = #{u_id}

select tr_id
from t_trains
where tr_name = #{tr_name}

mapper.update:
delete
from t_tickets
where u_id = #{u_id}
  and ti_id = #{ti_id}

mapper.select:
select count(*) > 0
from t_trains
where tr_id = #{tr_id}

SELECT COUNT(*) > 0
FROM t_router_trains AS rt
         JOIN t_router AS ro ON ro.r_id = rt.r_id
WHERE rt.tr_id = #{tr_id} ro.r_current_station = #{station}
   OR ro.r_next_station=#{station}

SELECT rt.rtr_date
FROM t_router_trains AS rt
         JOIN t_router AS ro ON ro.r_id = rt.r_id
WHERE rt.tr_id = #{tr_id}
  and ro.r_next_station = #{station}

SELECT rt.rtr_arrival_time
FROM t_router_trains AS rt
         JOIN t_router AS ro ON ro.r_id = rt.r_id
WHERE rt.tr_id = #{tr_id}
  and ro.r_next_station = #{station}

SELECT *
FROM t_trains
where tr_name = #{tr_name}

SELECT *
FROM t_trains
where tr_id = #{tr_id}

SELECT *
FROM t_station
where s_name = #{s_name}

SELECT *
FROM t_station
where s_id = #{s_id}
